import React from 'react';
// import {HPForms} from './Forms/HPForms';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import '../styles/Homepage_style.css'

const HPForms = props => (
    <Form>
        <h3>{props.title}</h3>
        <Form.Group>
            <Form.Label className='formLabel'>{props.formLabel1}</Form.Label>
            <Form.Control
                style={{ backgroundColor: '#ECECEC' }}
                type={props.typeForm1} />

            <Form.Label className='formLabel'>{props.formLabel2}</Form.Label>
            <Form.Control
                style={{ backgroundColor: '#ECECEC' }}
                type={props.typeForm2} />
        </Form.Group>

        <Button variant="primary" type="submit">
            Valider
        </Button>
        {props.registering ? <a href='/register'>créez un compte</a> : <div></div>}
    </Form>
)

class Homepage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ifRegistering : Boolean
        }
    }

    render() {
        return (
            <div className='homepage'>
                <div>
                    <header>
                        <h1>Let's Trip</h1>
                    </header>

                    <article>
                        <h3>Le Lorem Ipsum est simplement du faux texte <br />
                            employé dans la composition et la mise en page <br /> avant impression.</h3>

                            <p>Le Lorem Ipsum est le faux texte standard de l'imprimerie depuis les années 1500, 
                                quand un imprimeur anonyme assembla <br/> ensemble des morceaux de texte pour réaliser 
                                un livre spécimen de polices de texte.</p>
                    </article>

                    <div className="homepage-forms">
                        <HPForms 
                            title="Rejoindre une Roadmap"
                            formLabel1="Nom de la Roadmap"
                            formLabel2="Mot de passe de la Roadmap"
                            typeForm1='nameRoadmap'
                            typeForm2='passwordRoadmap'
                            registering={this.state.ifRegistering(false)}
                            />
                    <span>ou</span>
                    <HPForms 
                            title="Connectez-vous"
                            formLabel1="E-mail"
                            formLabel2="Mot de passe"
                            typeForm1='email'
                            typeForm2='password'
                            registering={this.state.ifRegistering(true)}
                            />
                    </div>
                </div>
            </div>
        )
    }
}

export default Homepage;
