import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import {
    NavLink
} from "react-router-dom";

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach(
        (val) => val.length > 0 && (valid = false)
    );
    return valid;
}

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            pseudo: '',
            lastName: '',
            firstName: '',
            email: '',
            password: '',
            errors: {
                pseudo: '',
                lastName: '',
                firstName: '',
                email: '',
                password: ''
            }
        }
    }

    handleChange(event) {
        event.preventDefault();
        let errors = this.state.errors
        const { name, value } = event.target;
        switch (name) {
            case 'pseudo':
                errors.pseudo =
                    value.length < 2
                        ? 'Votre pseudo doit contenir au moins 2 lettres'
                        : '';
                break;
            case 'firstName':
                errors.firstName =
                    value.length < 2
                        ? 'Votre prénom doit contenir au moins 2 lettres'
                        : '';
                break;
            case 'lastName':
                errors.lastName =
                    value.length < 2
                        ? 'Votre nom de famille doit contenir au moins 2 lettres'
                        : '';
                break;
            case 'email':
                errors.email =
                    validEmailRegex.test(value)
                        ? ''
                        : 'Entrer un e-mail valide';
                break;
            case 'password':
                errors.password =
                    value.length < 8
                        ? '8 caractères minimum pour le mot de passe'
                        : '';
                break;
            default:
                break;
        }

        this.setState({ errors, [name]: value });
    }

    handleSubmit(event) {
        event.preventDefault();
        const newInscription = {
            pseudo: this.state.pseudo,
            firstName: this.state.firstName,
            lastname: this.state.lastName,
            email: this.state.email,
            password: this.state.password
        }
        if (validateForm(this.state.errors)) {
            console.log('Valide :', newInscription)
        } else {
            console.error('Invalid Form')
        }
        this.setState({
            pseudo: '',
            firstName: '',
            lastName: '',
            email: '',
            password: ''
        })
    }

    render() {
        const { errors } = this.state;
        return (
            <div className="homepage-register-form">
                <h3 style={{ textAlign: 'center' }}>Rejoignez-nous</h3>


                <Form onSubmit={this.handleSubmit}>


                    <Form.Group className="homepage-register-formGroup-form">

                        <Form.Group >
                            <Form.Control
                                name='pseudo'
                                type="name"
                                placeholder="Pseudo *"
                                value={this.state.pseudo}
                                onChange={this.handleChange}
                                noValidate 
                                style={{width: "350px"}}/>
                            {errors.pseudo.length > 0 &&
                                <Form.Text className="text-muted">{errors.pseudo}</Form.Text>
                            }
                            {/* <Form.Text className="text-muted">votre pseudo doit avoir 2 qzdqrr fssq zqdsdzqsdq</Form.Text> */}

                            <Form.Control
                                name='firstName'
                                type="name"
                                placeholder="Prénom *"
                                value={this.state.firstName}
                                onChange={this.handleChange}
                                noValidate />
                            {errors.firstName.length > 0 &&
                                <Form.Text className="text-muted">{errors.firstName}</Form.Text>}


                            <Form.Control
                                name='lastName'
                                type="name"
                                placeholder="Nom *"
                                value={this.state.lastName}
                                onChange={this.handleChange}
                                noValidate />
                            {errors.lastName.length > 0 &&
                                <Form.Text className="text-muted">{errors.lastName}</Form.Text>}
                        </Form.Group>


                        <Form.Group>
                            <Form.Control
                                name='email'
                                type="email"
                                placeholder="E-mail *"
                                value={this.state.email}
                                onChange={this.handleChange}
                                noValidate 
                                style={{width: "350px"}}/>
                            {errors.email.length > 0 &&
                                <Form.Text className="text-muted">{errors.email}</Form.Text>}

                                <Form.Control
                                    name='password'
                                    type="password"
                                    placeholder="Password *"
                                    value={this.state.password}
                                    onChange={this.handleChange}
                                    noValidate />
                                {errors.password.length > 0 &&
                                    <Form.Text className="text-muted">{errors.password}</Form.Text>}


                                <Form.Control
                                    name='password'
                                    type="password"
                                    placeholder="Password *"
                                    value={this.state.password}
                                    onChange={this.handleChange}
                                    noValidate />
                                {errors.password.length > 0 &&
                                    <Form.Text className="text-muted">{errors.password}</Form.Text>}
                        </Form.Group>

                    </Form.Group>


                    <Button variant="primary" type="submit">
                        Inscription
                    </Button>

                    <NavLink to='/'>Vous êtes déjà membre ?</NavLink>
                </Form>
            </div>


        )
    }
}

export default Register;
