import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import {
    NavLink
} from "react-router-dom";

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            pseudo: '',
            lastName: '',
            firstName: '',
            email: '',
            password: '',
            errors: {
                pseudo: '',
                lastName: '',
                firstName: '',
                email: '',
                password: ''
            }
        }
    }

    render() {
        const { errors } = this.state;
        return (
            <div className="homepage-register-form">
                <h3 style={{ textAlign: 'center' }}>Rejoignez-nous</h3>


                <Form onSubmit={this.handleSubmit}>


                    <Form.Group className="homepage-register-formGroup-form">

                        <Form.Group>
                            <Form.Control
                                name='pseudo'
                                type="name"
                                placeholder="Pseudo *"
                                value={this.state.pseudo}
                                onChange={this.handleChange}
                                noValidate 
                                style={{width: "350px", border: "1px solid red"}}/>
                            {errors.pseudo.length > 0 &&
                                <Form.Text className="text-muted">{errors.pseudo}</Form.Text>
                            }
                            {/* <Form.Text className="text-muted">votre pseudo doit avoir 2 qzdqrr fssq zqdsdzqsdq</Form.Text> */}

                            <Form.Control
                                name='firstName'
                                type="name"
                                placeholder="Prénom *"
                                value={this.state.firstName}
                                onChange={this.handleChange}
                                noValidate />
                            {errors.firstName.length > 0 &&
                                <Form.Text className="text-muted">{errors.firstName}</Form.Text>}


                            <Form.Control
                                name='lastName'
                                type="name"
                                placeholder="Nom *"
                                value={this.state.lastName}
                                onChange={this.handleChange}
                                noValidate />
                            {errors.lastName.length > 0 &&
                                <Form.Text className="text-muted">{errors.lastName}</Form.Text>}
                        </Form.Group>


                        <Form.Group>
                            <Form.Control
                                name='email'
                                type="email"
                                placeholder="E-mail *"
                                value={this.state.email}
                                onChange={this.handleChange}
                                noValidate 
                                style={{width: "350px"}}/>
                            {errors.email.length > 0 &&
                                <Form.Text className="text-muted">{errors.email}</Form.Text>}

                                <Form.Control
                                    name='password'
                                    type="password"
                                    placeholder="Password *"
                                    value={this.state.password}
                                    onChange={this.handleChange}
                                    noValidate />
                                {errors.password.length > 0 &&
                                    <Form.Text className="text-muted">{errors.password}</Form.Text>}


                                <Form.Control
                                    name='password'
                                    type="password"
                                    placeholder="Password *"
                                    value={this.state.password}
                                    onChange={this.handleChange}
                                    noValidate />
                                {errors.password.length > 0 &&
                                    <Form.Text className="text-muted">{errors.password}</Form.Text>}
                        </Form.Group>

                    </Form.Group>


                    <Button variant="primary" type="submit">
                        Inscription
                    </Button>

                    <NavLink to='/'>Vous êtes déjà membre ?</NavLink>
                </Form>
            </div>


        )
    }
}

export default Register;
