import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import {
    NavLink
} from "react-router-dom";

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            pseudo: '',
            lastname: '',
            name: '',
            email: '',
            password: ''
        }
    }

    handleChange(event) {
        event.preventDefault();
        this.setState({
            [event.target.id]: event.target.value
        })
    }

    handleSubmit(event) {
        event.preventDefault();
        const newInscription = {
            pseudo : this.state.pseudo,
            name : this.state.name,
            lastname : this.state.lastname,
            email: this.state.email,
            password: this.state.password
        }
        console.log(newInscription)
    }

    render() {
        return (
            <Form onSubmit={this.handleSubmit}>
                <h3>Rejoignez-nous</h3>
                <Form.Group>
                    <Form.Control type="name" placeholder="Pseudo *" onChange={this.handleChange}/>
                    <Form.Control type="name" placeholder="Prénom *" onChange={this.handleChange}/>
                    <Form.Control type="name" placeholder="Nom *" onChange={this.handleChange}/>
                    <Form.Control type="email" placeholder="E-mail *"onChange={this.handleChange}/>
                    <Form.Control type="password" placeholder="Password *"onChange={this.handleChange}/>
                </Form.Group>


                <Button variant="primary" type="submit">
                    Inscription
                </Button>

                <NavLink to='/'>Vous êtes déjà membre ?</NavLink>
            </Form>
        )
    }
}

export default Register;
