import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import {
    NavLink
} from "react-router-dom";

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.handlePseudo = this.handlePseudo.bind(this)
        this.handleLastname = this.handleLastname.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleEmail = this.handleEmail.bind(this)
        this.handlePassword = this.handlePassword.bind(this)
        this.state = {
            pseudo: '',
            lastname: '',
            name: '',
            email: '',
            password: ''
        }
    }

    // Fonctions onChange des champs du formulaire
    handlePseudo(event) {
        event.preventDefault();
        this.setState({
            pseudo : event.target.value
        })
        console.log(this.state.pseudo)
    }

    handleLastname(event) {
        event.preventDefault();
        this.setState({
            lastname : event.target.value
        })
        console.log(this.state.lastname)
    }

    handleName(event) {
        event.preventDefault();
        this.setState({
            name : event.target.value
        })
        console.log(this.state.name)
    }

    handleEmail(event) {
        event.preventDefault();
        this.setState({
            email : event.target.value
        })
        console.log(this.state.email)
    }

    handlePassword(event) {
        event.preventDefault();
        this.setState({
            password : event.target.value
        })
        console.log(this.state.password)
    }

    handleSubmit(event) {

    }

    render() {
        return (
            <Form>
                <h3>Rejoignez-nous</h3>
                <Form.Group>
                    <Form.Control type="name" placeholder="Pseudo *" onChange={this.handlePseudo}/>
                    <Form.Control type="name" placeholder="Prénom *" onChange={this.handleName}/>
                    <Form.Control type="name" placeholder="Nom *" onChange={this.handleLastname}/>
                    <Form.Control type="email" placeholder="E-mail *" onChange={this.handleEmail}/>
                    <Form.Control type="password" placeholder="Password *" onChange={this.handlePassword}/>
                </Form.Group>


                <Button variant="primary" type="submit">
                    Inscription
                </Button>

                <NavLink to='/'>Vous êtes déjà membre ?</NavLink>
            </Form>
        )
    }
}

export default Register;
